create table if not exists mysite.threads (id int auto_increment, title text not null, `create-date` datetime not null, `last-modified-date` datetime not null, `last-rise-date` datetime not null, `res-count` integer not null default 1, unixtime integer, `max-line` integer default 1000 not null, `board-id` integer not null, primary key(id, unixtime))
